import React, { Component } from 'react'
import './itemnews.css'

export default class NewsItem extends Component {
  render() {

      let { title, description, imageUrl, newsUrl, author, date} = this.props;

    return (
      <div>

            <div className="card zoom my-3" style={{width: '18rem'}}>
                <img className="card-img-top" src={!imageUrl ?'https://soliloquywp.com/wp-content/uploads/2016/08/How-to-Set-a-Default-Featured-Image-in-WordPress.png' : imageUrl } alt="Card image cap"/>
                    <div className="card-body">
                        <h5 className="card-title">{title}...</h5>
                        <p className="card-text">{description}...</p>

                        <p className="card-text"><small class="text-muted">By {author? author : "Anonymous" } on {new Date(date).toGMTString.addHours()}</small></p>

                        <a href={newsUrl} target='_blank' className="btn btn-sm btn-dark">Read More</a>
                    </div>
            </div>



            
      </div>
    )
  }
}
