import React, { Component } from 'react'
import NewsItem from './NewsItem'


export default class News extends Component {
    articles = [
       
           
        ]

   
    constructor() {
        super();
        this.state = {
            articles: this.articles,
            loading: false
        }
    }

    

    async componentDidMount(){
        let url ='https://newsapi.org/v2/top-headlines?country=us&apiKey=e6c4cd6431c54892ba068ce4bef9a137'
        let data = await fetch(url)
        let parsedData = await data.json();
        // console.log(parsedData);
        this.setState({articles: parsedData.articles})


    }



    render() {
        return (
            
            <div className='container my-3 text-center'>
                <h2>WELCOME TO ABC NEWS</h2>


                <div className="row">
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title?element.title.slice(0,40):""} description={element.description?element.description.slice(0,20):""} imageUrl={element.urlToImage} newsUrl={element.url} />

                        </div>


                    })}


                </div>

            </div>

        )
    }
}
